package com.dnpass.java.Samples.Day2;

public class addsubmuldivrem {

	public static void main(String[] args) {
		int x=20,y=4;
		// TODO Auto-generated method stub
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
	}

}
